custom_css.post_content:
.top-header .logo-block&gt;a&gt;img {
    height: 140px;
}

.load-more-button-container {
    display: flex;
    justify-content: center;
    align-items: center;

}

.wp-block-button__link {
    color: #fff;
    background-color: #ee7778;
    border: none;
    border-radius: 28px;
    box-shadow: none;
    cursor: pointer;
    display: inline-block;
    font-size: 18px;
    padding: 12px 24px;
    text-align: center;
    text-decoration: none;
    overflow-wrap: break-word;
}

.wp-block-button__link:hover {
    color: #fff;
    background-color: #000;

}
